<script>
import anime from 'animejs'

export default {
  props: {
    user: { type: Object, required: true },
    prepDate: { type: Date, required: true }
  },
  computed: {
    endDate() {
      return new Date(this.user.profile.currentVisa.end)
    },
    percent() {
      const start = new Date(this.prepDate.setMonth(this.prepDate.getMonth() - 1)),
            end =  new Date(this.endDate.setMonth(this.endDate.getMonth() + 1)),
            today = new Date();

      const startDate = start.getTime(),
            endDate = end.getTime(),
            todayDate = today.getTime();

      // Get the total possible timestamp value
      const total = endDate - startDate

      // Get the current value
      const current = todayDate - startDate

      // Get the percentage (+6 to match up with svg path)
      const percentage = Math.floor((current / total) * 100) + 6

      // Don't return percentage that's smaller than 0 or larger than 100 
      if(percentage <= 0) {
        return 0
      } else if(percentage >= 100) {
        return 100
      } else {
        return percentage
      }       
    }
  },
  mounted() {
    if(this.percent > 0) {
      const path = anime.path('#path', this.percent)

      anime({
        targets: '#point',
        translateX: path('x'),
        translateY: path('y'),
        easing: 'easeInOutQuint',
        duration: 1500,
        delay: 1000
      })
    }
  }
}
</script>

